definition.script:
def transformIntoStep(inputString) {
    return {
        //stage(inputString) {
            //println inputString
            build job: 'BIOFORMATS-test-folder', parameters: [
            [$class: 'StringParameterValue', name: 'FORMAT_NAME', value: inputString]]
        //}
    }
}

def stepsForParallel = []

node("docker") {
    stage('List') {
        def data_dir = System.getenv()['REPO_CURATED']
        def cmd = "find ${data_dir} -mindepth 1 -maxdepth 1 -type d "
        list = sh(returnStdout: true, script: cmd).trim()
        def formats = list.split()
        stepsForParallel = formats.collectEntries {
            ["handling ${it}" : transformIntoStep(it.substring(it.lastIndexOf('/') + 1))]
        }
    }

}

stage('Run') {
    parallel stepsForParallel
}
